# [139] 单词拆分
#
from typing import List
# @lc code=start
class Solution:
    def wordBreak(self, s: str, wordDict: List[str]) -> bool:
        w_cnt = len(wordDict)
        s_len = len(s)
        dp = [False for i in range(s_len+1)] # dp[i] 代表s的前i个字符能不能有wordDict组成
        dp[0] = True # 起始值
        for i in range(1, s_len + 1):
            for word in wordDict:
                w_len = len(word)
                if w_len <= i and dp[i - w_len] and s[i - w_len : i] == word:
                    dp[i] = True
                    break

        return dp[s_len]
# @lc code=end

